import React, {useState, useEffect} from 'react';
import axios from "axios"

import Card from "../../Card/Card";

const List = (props) => {

    const state = props.pakemonu;

    const [pakemons, setPokemons] = useState([]);
    const [load, setLoad] = useState(false);

    const [nextPageLink, setNextLink] = useState('');
    const [prevPageLink, setPrevLink] = useState('');

    const [type, setType] = useState('');
    const [stats, setStats] = useState([]);

    const getData = async () => {

        try {

            const link = `https://pokeapi.co/api/v2/pokemon/`;
            const dataPakemons = await axios.get(link);

            setPokemons(dataPakemons.data);

            console.log(dataPakemons);

        } catch (e) {
            console.log(e);
        }
    }

    useEffect(() => {

        getData();
    }, []);

    let pk = dataPakemons.data.map(pakemon => (
        <Card
                                            name={pokemon.name}
                                            img={pokemon.url}
                                            type={pokemon.name} />
    ));

    return (
        <div>
            {
                pk
            }
        </div>

    );


}

export default List
