import React, {useState} from 'react';
import axios from "axios"

import {Multiselect} from "multiselect-react-dropdown";
import CardPakemon from "../../Card/CardPakemon";

const Tags = (props) => {

    const data = [
        {Type: "psychic", id: 1},
        {Type: "aqwesd", id: 2},
        {Type: "qwe", id: 1}
    ]
    const [types] = useState(data);

    const options = [];
    const [selected1, setSelected1] = useState(1);
    const [selected, setSelected] = useState([]);
    const [dataPakemonu, setDataPakemonu] = useState([]);


    const [type, setType] = useState('');
    const [stats, setStats] = useState([]);

    const getFilterPokemon = async () => {

        const arrayPakemon = [];

        try{

            // url api pakemen using axois
            const urlAPI = `https://pokeapi.co/api/v2/type/${selected1}`;
            const resultPakemon = await axios.get(urlAPI);

            // insert to array searched pakemon
            arrayPakemon.push(resultPakemon.data);

            // set data
            setSelected(resultPakemon.data.types);
            setDataPakemonu(arrayPakemon);

            // show console result
            console.log(resultPakemon);

        }catch(e){
            console.log(e);
        }
    }

    const handleChange = (e) => {

        setSelected1(e.target.value.toLowerCase());
    }
    const filterOptions = (e) => {
        // Triggered for filter input changes
        e.preventDefault();

        getFilterPokemon();
    }

    return (
        <div className="text-left">

            <div className="container d-flex justify-content-between border-bottom mb-5">
                <div className="left">
                    <h3>Фільтр</h3>
                    <p className="text-secondary">Фільтр пакемона за типом</p>
                </div>
                <div className="form-search">
                    <form>
                        <Multiselect options={types} displayValue="Type" onChange={handleChange} />
                        <button onClick={filterOptions}>Get</button>
                    </form>
                </div>
                <div className="container">
                    {
                        setSelected
                    }
                </div>
            </div>

        </div>
    );
}

export default Tags
