import React, { Component } from 'react';
import axios from "axios";
import Card from "../../Card/Card";
import Search from "../Search/Search";
import Tags from "../Tags/Tags";

import state2 from "../../../State/pakemon";

class List2 extends Component {
    state = state2
    // state = {
    //     url: 'https://pokeapi.co/api/v2/pokemon/',
    //     pakemonu: null
    // }

    // const [nextBtn, setSearchPakemonu] = useState('');

    async componentDidMount() {

        const result = await axios.get(this.state.url);
        this.setState({pakemonu: result.data['results']});
    }

    render() {
        return (
            <React.Fragment>
                {
                    this.state.pakemonu ? (

                        <div className="d-flex2 justify-content-between2">
                            {/*<Search/>*/}
                            {/*<Tags/>*/}
                            <div className="col-md-">

                            </div>

                            <div className="col-md-">
                                <div className="row">

                                    {
                                        this.state.pakemonu.map(pokemon => <Card
                                            name={pokemon.name}
                                            img={pokemon.url}
                                            type={pokemon.name}
                                        />)
                                    }
                                </div>


                            </div>
                        </div>

                    ) : (
                        <p>Завантаження пакемонів ...</p>
                    )
                }
            </React.Fragment>
        );
    }
}

export default List2
